Page._content:
.home-hero {
  display: grid;
  grid-template-columns: 200px 1fr;
  gap: 2rem;
  align-items: center;
  margin: 2.5rem 0 3rem;
}

.home-hero-media {
  display: flex;
  justify-content: center;
}

.home-avatar {
  width: 200px;
  height: 200px;
  border-radius: 999px;
  object-fit: cover;
  border: 3px solid var(--third-background-color);
  box-shadow: var(--redefine-box-shadow);
}

.home-eyebrow {
  font-size: 0.95rem;
  text-transform: uppercase;
  letter-spacing: 0.08em;
  color: var(--second-text-color);
  margin: 0 0 0.4rem;
}

.home-name {
  font-size: 2.3rem;
  margin: 0.2rem 0 0.4rem;
}

.home-name-alt {
  font-size: 1rem;
  font-weight: 400;
  color: var(--second-text-color);
  margin-left: 0.35rem;
}

.home-affiliation {
  margin: 0.2rem 0 0.8rem;
  color: var(--third-text-color);
}

.home-actions {
  display: flex;
  flex-wrap: wrap;
  gap: 0.75rem;
}

.home-button {
  display: inline-flex;
  align-items: center;
  gap: 0.4rem;
  padding: 0.5rem 1rem;
  border-radius: 999px;
  border: 1px solid var(--shadow-color-1);
  background: var(--background-color);
  color: var(--default-text-color);
  text-decoration: none;
  transition: transform 0.2s ease, box-shadow 0.2s ease, color 0.2s ease;
}

.home-button:hover {
  transform: translateY(-1px);
  box-shadow: var(--redefine-box-shadow-flat);
  color: var(--primary-color);
}

.home-button.primary {
  background: var(--primary-color);
  color: var(--invert-text-color);
  border-color: var(--primary-color);
}

.home-button.primary:hover {
  color: var(--invert-text-color);
}

.home-button.ghost {
  background: var(--second-background-color);
}

.home-section {
  margin-top: 3rem;
}

.home-section h2 {
  margin-bottom: 1rem;
}

.home-highlight {
  display: flex;
  gap: 0.75rem;
  align-items: center;
  padding: 1rem 1.2rem;
  border-radius: 14px;
  background: var(--third-background-color);
  border: 1px solid var(--shadow-color-1);
  margin-top: 1.2rem;
}

.home-highlight-icon {
  width: 40px;
  height: 40px;
  border-radius: 12px;
  display: inline-flex;
  align-items: center;
  justify-content: center;
  background: var(--primary-color);
  color: var(--invert-text-color);
  font-size: 1.1rem;
}

.home-card {
  background: var(--second-background-color);
  border: 1px solid var(--shadow-color-1);
  border-radius: 18px;
  padding: 1.5rem;
  box-shadow: var(--redefine-box-shadow-flat);
}

.home-card + .home-card {
  margin-top: 1.5rem;
}

.home-embed {
  position: relative;
  width: 100%;
  aspect-ratio: 16 / 9;
  overflow: hidden;
  border-radius: 14px;
  background: var(--third-background-color);
  margin-top: 1rem;
}

.home-embed iframe {
  width: 100%;
  height: 100%;
  border: 0;
}

.home-meta {
  display: flex;
  flex-wrap: wrap;
  gap: 0.5rem;
  margin-top: 0.8rem;
}

.home-chip {
  display: inline-flex;
  align-items: center;
  gap: 0.35rem;
  padding: 0.25rem 0.7rem;
  border-radius: 999px;
  background: var(--background-color);
  border: 1px solid var(--shadow-color-1);
  font-size: 0.8rem;
  color: var(--second-text-color);
}

.home-grid {
  display: grid;
  gap: 1rem;
  margin-top: 1rem;
}

.home-grid.cols-3 {
  grid-template-columns: repeat(3, minmax(0, 1fr));
}

.home-grid.cols-2 {
  grid-template-columns: repeat(2, minmax(0, 1fr));
}

.home-video {
  width: 100%;
  border-radius: 14px;
  display: block;
  background: var(--third-background-color);
}

.home-caption {
  display: block;
  margin-top: 0.5rem;
  color: var(--second-text-color);
  font-size: 0.85rem;
}

.home-timeline {
  display: flex;
  flex-direction: column;
  gap: 1.25rem;
  margin-top: 1.5rem;
  border-left: 2px solid var(--shadow-color-1);
  padding-left: 1.5rem;
}

.home-timeline-item {
  position: relative;
  display: grid;
  grid-template-columns: 140px 1fr;
  gap: 1rem;
  align-items: center;
}

.home-timeline-item::before {
  content: "";
  position: absolute;
  left: -1.65rem;
  top: 0.4rem;
  width: 10px;
  height: 10px;
  border-radius: 50%;
  background: var(--primary-color);
}

.home-timeline-item.current::before {
  box-shadow: 0 0 0 4px var(--background-color);
}

.home-timeline-date {
  font-size: 0.85rem;
  color: var(--third-text-color);
}

.home-timeline-content {
  display: flex;
  align-items: center;
  gap: 0.9rem;
}

.home-timeline-logo {
  width: 44px;
  height: 44px;
  object-fit: contain;
  padding: 6px;
  border-radius: 12px;
  border: 1px solid var(--shadow-color-1);
  background: var(--background-color);
}

.home-timeline-title {
  font-weight: 600;
}

.home-timeline-subtitle {
  font-size: 0.9rem;
  color: var(--second-text-color);
}

.home-news-list {
  display: flex;
  flex-direction: column;
  gap: 1rem;
  margin-top: 1.2rem;
}

.home-news-item {
  display: grid;
  grid-template-columns: 110px 1fr;
  gap: 1rem;
  align-items: start;
}

.home-news-date {
  display: inline-flex;
  justify-content: center;
  align-items: center;
  padding: 0.35rem 0.6rem;
  border-radius: 999px;
  background: var(--third-background-color);
  border: 1px solid var(--shadow-color-1);
  font-size: 0.8rem;
  color: var(--second-text-color);
}

.home-muted {
  color: var(--second-text-color);
}

.home-inline-media {
  margin-top: 0.6rem;
}

.home-inline-media video {
  width: 100%;
  max-width: 560px;
  border-radius: 12px;
  background: var(--third-background-color);
}

.home-award {
  display: flex;
  flex-direction: column;
  justify-content: center;
  gap: 0.5rem;
  padding: 1rem;
  border-radius: 14px;
  background: var(--background-color);
  border: 1px solid var(--shadow-color-1);
}

.home-github {
  padding: 1rem;
  border-radius: 14px;
  background: var(--second-background-color);
  border: 1px solid var(--shadow-color-1);
}

.home-github img {
  width: 100%;
  height: auto;
  display: block;
}

.page-template-content > h1:empty {
  display: none;
}

@media (max-width: 900px) {
  .home-hero {
    grid-template-columns: 1fr;
    text-align: center;
  }

  .home-actions {
    justify-content: center;
  }

  .home-timeline-item {
    grid-template-columns: 1fr;
  }

  .home-news-item {
    grid-template-columns: 1fr;
  }
}

@media (max-width: 768px) {
  .home-grid.cols-3,
  .home-grid.cols-2 {
    grid-template-columns: 1fr;
  }
}

